import React, { useState } from "react";
import { View, Text, StyleSheet, TextInput, FlatList } from "react-native";
import { SafeAreaView } from "react-native-safe-area-context";
import Button from "../components/Button";
import Card from "../components/Card";
import Header from "../components/Header";
import Footer from "../components/Footer";

export default function TripCreationScreen() {
  const [destination, setDestination] = useState("Enter Destination");
  const [description, setTripDescription] = useState("Enter Trip Description");

  const members = [
    { id: "1", name: "Abdiaziz" },
    { id: "2", name: "Callum" },
    { id: "3", name: "Mark" },
    { id: "4", name: "Serkan"},
    { id: "5", name: "Stefano"},
  ];

  const renderMembers = ({ item }) => (
    <Card
      memberName={item.name}
      onPress={() => console.log(`MemberCard Pressed: ${item.name}`)}
    />
  );

  return (
    <View style={styles.container}>
      <Header title="Trippy" />
      <View style={styles.content}>
        <Text style={styles.text}>Create Trip</Text>
        <TextInput
          style={styles.input}
          onChangeText={setDestination}
          placeholder="Enter Destination"
          value={destination}
        />
        <TextInput
          style={[styles.input, styles.multiLineText]}
          onChangeText={setTripDescription}
          placeholder="Trip Description"
          value={description}
          multiline
        />
        <Text style={styles.subText}>Add Friends</Text>
        <FlatList
          data={members}
          renderItem={renderMembers}
          keyExtractor={(item) => item.id.toString()}
          contentContainerStyle={styles.cardsContainer}
        //   scrollEnabled={false}

        />
        <Button
          title="Add Member"
          onPress={() => console.log("Button Add Member")}
          style={{ alignSelf: "center", paddingHorizontal: 40 }}
        />
        <Button
          title="Done"
          onPress={() => console.log("Button Done")}
          style={{ alignSelf: "center", paddingHorizontal: 40 }}
        />
      </View>
      <Footer text="Icons display here" style={styles.footer} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#F7F7F7",
  },
  content: {
    flex: 1,
    paddingHorizontal: 20,
  },
  text: {
    fontSize: 24,
    color: "#24565C",
    fontWeight: "bold",
    textAlign: "center",
    marginBottom: 20,
  },
  subText: {
    fontSize: 18,
    color: "#666",
    marginVertical: 10,
    textAlign: "center",
  },
  input: {
    height: 40,
    marginVertical: 10,
    borderWidth: 1,
    borderColor: "#ccc",
    padding: 10,
    borderRadius: 5,
  },
  multiLineText: {
    minHeight: 100,
    textAlignVertical: "top",
  },
  cardsContainer: {
    paddingVertical: 10,
  },
  footer: {
    borderTopWidth: 1,
    borderColor: "#ccc",
    padding: 10,
  },
});
