import React, {useState} from "react";
import { View, Text, StyleSheet, TextInput } from "react-native";
import { ScrollView,FlatList } from "react-native-web";
// import DatePicker from "react-native-date-picker";

import {SafeAreaView, SafeAreaProvider} from 'react-native-safe-area-context';
import Button from "../components/Button"
import Card from "../components/Card";

import Header from "../components/Header";
import Footer from "../components/Footer";


export default function TripCreationScreen() {
    const {destination, setDestination} = useState('Enter Destination');
    const members = [
        {
            id: "1",
            name: "Abdiaziz"
        },
        {
            id: "2",
            name: "Callum"
        },
        {
            id: "3",
            name: "Mark",
        },
        {
            id: "3",
            name: "Serkan"
        },
        {
            id: "4",
            name: "Stefano"
        },
    ];

    const renderMembers = ({item})=>{
        <Card
            memberName = {item.name}
            onPress={()=> console.log(`MemberCard Pressed: ${item.name}`)}
        />
    }

  return (
    <View style={styles.container}>
      <Header title="Trippy" />
      <View style={styles.content}>
        <Text style={styles.text}>Create Trip</Text>

          <TextInput 
            style={styles.input}
            onChangeText={setDestination}
            placeholder='Enter Destination'
            value={destination}
          />
        <TextInput style={[styles.input, styles.multiLineText]} placeholder="Trip Description" multiline />

        <Text>Add Friends</Text>
        <Button title="Add Member"
          onPress={() => console.log("Button Add Member")}
          style={{ paddingHorizontal: 40, marginHorizontal: "auto" }}
        />
        <Button title="Done"
          onPress={() => console.log("Button Done")}
          style={{ paddingHorizontal: 40, marginHorizontal: "auto" }}
        />
      </View>
      <View>
      <FlatList
        data={members}
        renderItem={renderMembers}
        keyExtractor={(item)=>item.id.toString()}
        numColumns={1}
        contentContainerStyle={styles.cardsContainer}
        scrollEnabled={false}
      />


      </View>
      {/* Footer Component */}
      <Text style={styles.title}>Footer Component</Text>
      <Footer text="Icons display here" style={styles.footer} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#F7F7F7",
  },
  content: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    paddingHorizontal: 20,
  },
  text: {
    fontSize: 24,
    color: "#24565C",
    fontWeight: "bold",
  },
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
  },
  multiLineText:{
    minHeight: 100,
    textAlign:"top",
  },
});
